refsection.programlisting:
H = syslin('c',352*poly(-5,'s')/poly([0,0,2000,200,25,1],'s','c'));
clf
evans(H,100)
sgrid()

// All labels in violet
sGrid = gca().children.children.children;
sGrid(sGrid.type=="Text").font_foreground = color("light slate blue");

