// Подготовка данных различных типов: b = rand(2,3) < 0.5; // логические значения i = uint64(grand(3,4,2,"uin",0,100)); // гиперматрица кодированных целых чисел c = grand(3,3,"uin",-10,10); c=c+c'*%i; // комплексые числа p = (1-%z).^(0:3); // полиномы r = p/(1+%z); // рациональные значения sp = sprand(10,15,0.1); // разрежённая матрица чисел spb = sp<0.5; // разрежённая матрица логических значений t = ["This" "software" ; "is" "Scilab"];// текстовые строки g = [gdf() gda()]; // графические дескрипторы function Test() // сценарий disp("listvarinfile() test for macros") end M = linspace; // сценарий L = list(%f, int8(-23),, %i-%z, list("abc",%e)); // простой список C = {"abc" %t ; %z list(1/%z, 4)}; // cell-массив S(4,2).r = %pi; // массив структур vars = list("b","i","c","p","r","sp","spb","t","g","Test","M","L","C","S");
