// x and y initialisation
x = linspace(0, 2, 100);
y = exp(-x).*cos(10*x) + 0.2*rand(1,length(x));
// create 2D scatter subplot
subplot(2, 1, 1)
scatter(gca(), x, y);
// create 2D scatter subplot
subplot(2, 1, 2)
scatter(gca(), x, y, "fill", "s");

// x and y initialisation
t = linspace(0,1,200);
x = t .* cos(10*%pi*t);
y = t .* sin(10*%pi*t);
// create 2D scatter plot
p = scatter(x, y);

// modify polyline
p.thickness = 0.5;
p.mark_foreground = color("darkblue");
p.mark_background = color("darkcyan");
