refsection.programlisting:
x = [0 1];
y = (0:0.025:1)';
y = [y y+0.02]';

th = 0:2:360;
xe = 4*cosd(th); xe = (0.1:0.05:1)' * xe;
ye = sind(th);   ye = (0.1:0.05:1)' * ye;

usecanvas(%t);

i = 0;
for aa = ['off' '4x' '16x']
    f = scf(i);
    f.axes_size = [300 600];
    f.figure_position(1) = 100+300*i;
    f.anti_aliasing = aa;  // <<<<<<<<
    clf
    subplot(2,1,1)
    plot2d(x,y)
    subplot(2,1,2)
    plot2d(xe',ye')
    title(".anti_aliasing = ''"+aa+"''", "fontsize",4);
    i = i+1;
end
