refsection.programlisting:
// Cas des polynômes
x = poly(0,'x');
p1 = (x+1)*(x-3)^5;
p2 = (x-2)*(x-3)^3;
[pgcd,U] = bezout(p1,p2)
det(U)
clean([p1,p2]*U)
ppcm = p1*U(1,2)
lcm([p1,p2])

// Cas des entiers décimaux
i1 = 2*3^5;
i2 = 2^3*3^2;
[thegcd, U] = bezout(i1, i2)
V = [2^2*3^5 2^3*3^2 2^2*3^4*5];
[thegcd, U] = gcd(V)
V*U
lcm(V)

// Cas des entiers encodés
i1 = int32(2*3^5);
i2 = int32(2^3*3^2);
[thegcd, U] = bezout(i1, i2)
V = int32([2^2*3^5 2^3*3^2 2^2*3^4*5]);
[thegcd, U] = gcd(V)
V*U
lcm(V)
