refsection.programlisting:
rand('normal');rand('seed',0);
x = rand(1:1024-33+1);

// делаем фильтр нижних частот с помощью eqfir
nf = 33; bedge = [0 .1;.125 .5]; des = [1 0]; wate = [1 1];
h =eqfir(nf, bedge, des, wate);

// фильтруем белые данные для получения цветных данных
h1 = [h 0*ones(1:max(size(x))-1)];
x1 = [x 0*ones(1:max(size(h))-1)];
hf = fft(h1,-1);
xf = fft(x1,-1);
y = real(fft(hf.*xf,1));

// строим АЧХ фильтра
h2 = [h 0*ones(1:968)];
hf2 = fft(h2,-1);
hf2 = real(hf2.*conj(hf2));
hsize = max(size(hf2));
fr = (1:hsize) / hsize;
plot(fr, log(hf2));

// пример pspect
sm = pspect(100,200,'tr',y);
smsize = max(size(sm));
fr = (1:smsize) / smsize;
plot(fr, log(sm),'r');
rand('unif');
